const calculateStats = (values, teamIds) => {
    const sortedData = values.map((v, i) => ({ val: v, team: teamIds[i] })).sort((a, b) => a.val - b.val);
    const min = sortedData[0];
    const max = sortedData[sortedData.length - 1];
    const sum = values.reduce((a, b) => a + b, 0);
    const mean = sum / values.length;
    const variance = values.reduce((acc, val) => acc + Math.pow(val - mean, 2), 0) / values.length;
    const stdDev = Math.sqrt(variance);
    const median = values.length % 2 === 0 ? (sortedData[values.length/2 - 1].val + sortedData[values.length/2].val) / 2 : sortedData[Math.floor(values.length/2)].val;

    return {
        min, max,
        mean: mean.toFixed(1),
        median: median.toFixed(1),
        stdDev: stdDev.toFixed(2),
        range: (max.val - min.val).toFixed(1),
        count: values.length
    };
};

